import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Importez Link depuis react-router-dom

const AddEmployeePage = ({ history }) => {
  const [newEmployeeData, setNewEmployeeData] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: '',
    departement: '',
    dateEmbauche: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmployeeData({ ...newEmployeeData, [name]: value });
  };

  const handleAddEmployee = async () => {
    try {
        const response = await axios.post('http://localhost:3001/api/employee', newEmployeeData); // L'URL doit correspondre à '/employee'
        const { redirectTo } = response.data;
        if (redirectTo) {
            history.push(redirectTo); // Rediriger vers la liste des employés après l'ajout
        }
    } catch (error) {
        console.error('Erreur lors de l\'ajout de l\'employé:', error);
    }
};


  return (
    <div>
      <h2>Ajouter un employé</h2>
      <form onSubmit={handleAddEmployee}>
        <label>
          Nom:
          <input type="text" name="nom" value={newEmployeeData.nom} onChange={handleInputChange} />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={newEmployeeData.prenom} onChange={handleInputChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={newEmployeeData.email} onChange={handleInputChange} />
        </label>
        <label>
          Poste:
          <input type="text" name="poste" value={newEmployeeData.poste} onChange={handleInputChange} />
        </label>
        <label>
          Département:
          <input type="text" name="departement" value={newEmployeeData.departement} onChange={handleInputChange} />
        </label>
        <label>
          Date d'embauche:
          <input type="date" name="dateEmbauche" value={newEmployeeData.dateEmbauche} onChange={handleInputChange} />
        </label>
        {/* Utilisez Link pour créer le lien vers EmployeeComponent */}
        <Link to="/employees"><button type="button">Annuler</button></Link>
        <button type="submit">Ajouter</button>
        <Link to={`/updateEmployee/${employee._id}`}></Link>
      </form>
    </div>
  );
};

export default AddEmployeePage;
