import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmployeeComponent = () => {
  const [employees, setEmployees] = useState([]);
  const [newEmployeeData, setNewEmployeeData] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: '',
    departement: '',
    dateEmbauche: ''
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/employee');
      setEmployees(response.data);
    } catch (error) {
      console.error('Erreur lors du chargement des employés:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmployeeData({ ...newEmployeeData, [name]: value });
  };

  const handleAddEmployee = async () => {
    try {
      await axios.post('http://localhost:3001/api/employee', newEmployeeData);
      fetchEmployees(); // Recharger la liste des employés après l'ajout
      setNewEmployeeData({
        nom: '',
        prenom: '',
        email: '',
        poste: '',
        departement: '',
        dateEmbauche: ''
      }); // Réinitialiser les données du nouvel employé
    } catch (error) {
      console.error('Erreur lors de l\'ajout de l\'employé:', error);
    }
  };

  const handleDeleteEmployee = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/employee/${id}`);
      fetchEmployees(); // Recharger la liste des employés après la suppression
    } catch (error) {
      console.error('Erreur lors de la suppression de l\'employé:', error);
    }
  };

  const handleUpdateEmployee = async (id) => {
    // Implémentez la logique pour mettre à jour un employé ici
    console.log('Mise à jour de l\'employé avec l\'ID:', id);
  };

  return (
    <div>
      <h2>Liste des employés</h2>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Prénom</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee._id}>
              <td>{employee.nom}</td>
              <td>{employee.prenom}</td>
              <td>{employee.email}</td>
              <td>
                <button onClick={() => handleDeleteEmployee(employee._id)}>Supprimer</button>
                <button onClick={() => handleUpdateEmployee(employee._id)}>Mettre à jour</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Ajouter un employé</h2>
      <form onSubmit={handleAddEmployee}>
        <label>
          Nom:
          <input type="text" name="nom" value={newEmployeeData.nom} onChange={handleInputChange} />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={newEmployeeData.prenom} onChange={handleInputChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={newEmployeeData.email} onChange={handleInputChange} />
        </label>
        <label>
          Poste:
          <input type="text" name="poste" value={newEmployeeData.poste} onChange={handleInputChange} />
        </label>
        <label>
          Département:
          <input type="text" name="departement" value={newEmployeeData.departement} onChange={handleInputChange} />
        </label>
        <label>
          Date d'embauche:
          <input type="date" name="dateEmbauche" value={newEmployeeData.dateEmbauche} onChange={handleInputChange} />
        </label>
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
};

export default EmployeeComponent;
