import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const AddAbsencePage = () => {
  const history = useHistory();
  const [newAbsenceData, setNewAbsenceData] = useState({
    date: '',
    motif: '',
    heuresManquees: '',
    employe: '' // Assurez-vous de fournir l'identifiant ObjectId de l'employé
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewAbsenceData({ ...newAbsenceData, [name]: value });
  };

  const handleAddAbsence = async () => {
    try {
      await axios.post('http://localhost:3001/api/absence', newAbsenceData);
      // Redirection vers la liste des absences après l'ajout
      history.push('/absences');
    } catch (error) {
      console.error('Erreur lors de l\'ajout de l\'absence:', error);
    }
  };

  return (
    <div>
      <h2>Ajouter une absence</h2>
      <form onSubmit={handleAddAbsence}>
        <label>
          Date:
          <input type="date" name="date" value={newAbsenceData.date} onChange={handleInputChange} />
        </label>
        <label>
          Motif:
          <input type="text" name="motif" value={newAbsenceData.motif} onChange={handleInputChange} />
        </label>
        <label>
          Heures manquées:
          <input type="number" name="heuresManquees" value={newAbsenceData.heuresManquees} onChange={handleInputChange} />
        </label>
        {/* Assurez-vous de fournir l'identifiant ObjectId de l'employé */}
        <label>
          Employé:
          <input type="text" name="employe" value={newAbsenceData.employe} onChange={handleInputChange} />
        </label>
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
};

export default AddAbsencePage;
