import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';

const UpdateEmployeePage = () => {
  const [employeeData, setEmployeeData] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: '',
    departement: '',
    dateEmbauche: ''
  });

  const history = useHistory();
  const { id } = useParams();

  useEffect(() => {
    fetchEmployee();
  }, []);

  const fetchEmployee = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/employee/${id}`);
      setEmployeeData(response.data);
    } catch (error) {
      console.error('Erreur lors du chargement des détails de l\'employé:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData({ ...employeeData, [name]: value });
  };

  const handleUpdateEmployee = async () => {
    try {
      await axios.put(`http://localhost:3001/api/employee/${id}`, employeeData);
      history.push('/employees'); // Rediriger vers la liste des employés après la mise à jour
    } catch (error) {
      console.error('Erreur lors de la mise à jour de l\'employé:', error);
    }
  };

  return (
    <div>
      <h2>Modifier les détails de l'employé</h2>
      <form onSubmit={handleUpdateEmployee}>
        <label>
          Nom:
          <input type="text" name="nom" value={employeeData.nom} onChange={handleInputChange} />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={employeeData.prenom} onChange={handleInputChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={employeeData.email} onChange={handleInputChange} />
        </label>
        <label>
          Poste:
          <input type="text" name="poste" value={employeeData.poste} onChange={handleInputChange} />
        </label>
        <label>
          Département:
          <input type="text" name="departement" value={employeeData.departement} onChange={handleInputChange} />
        </label>
        <label>
          Date d'embauche:
          <input type="date" name="dateEmbauche" value={employeeData.dateEmbauche} onChange={handleInputChange} />
        </label>
        <button type="submit">Enregistrer les modifications</button>
      </form>
    </div>
  );
};

export default UpdateEmployeePage;
