import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddLeavePage = () => {
  const navigate = useNavigate();
  const [newLeaveData, setNewLeaveData] = useState({
    type: '',
    dateDebut: '',
    dateFin: '',
    statut: 'En attente', // Default status is set to "En attente"
    employe: '', // You need to get the selected employee ID here
    dateDemande: new Date().toISOString().split('T')[0] // Default request date set to the current date
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewLeaveData({ ...newLeaveData, [name]: value });
  };

  const handleAddLeave = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior
    try {
      const response = await axios.post('http://localhost:3001/api/leave', newLeaveData);
      console.log(response.data);
      navigate('/leave');
    } catch (error) {
      console.error('Erreur lors de l\'ajout de la demande de congé:', error);
    }
  };

  return (
    <div>
      <h2>Ajouter une demande de congé</h2>
      <form onSubmit={handleAddLeave}>
        <label>
          Type:
          <input type="text" name="type" value={newLeaveData.type} onChange={handleInputChange} />
        </label>
        <label>
          Date de début:
          <input type="date" name="dateDebut" value={newLeaveData.dateDebut} onChange={handleInputChange} />
        </label>
        <label>
          Date de fin:
          <input type="date" name="dateFin" value={newLeaveData.dateFin} onChange={handleInputChange} />
        </label>
        {/* The employee field should be filled with the selected employee ID */}
        <label>
          Employé:
          <input type="text" name="employe" value={newLeaveData.employe} onChange={handleInputChange} />
        </label>
        {/* The status field is hidden and defaults to "En attente" */}
        <input type="hidden" name="statut" value="En attente" />

        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
};

export default AddLeavePage;
