import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AddLeavePage = () => {
  const [newLeaveData, setNewLeaveData] = useState({
    type: '',
    dateDebut: '',
    dateFin: '',
    statut: 'En attente', // Par défaut, le statut est défini sur "En attente"
    employe: '', // Vous devrez obtenir l'ID de l'employé sélectionné ici
    dateDemande: new Date().toISOString().split('T')[0] // Date de demande automatique définie sur la date actuelle
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewLeaveData({ ...newLeaveData, [name]: value });
  };

  const handleAddLeave = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/leave', newLeaveData);
      console.log(response.data);
      // Redirigez l'utilisateur vers la page des demandes de congé ou une autre page si nécessaire
    } catch (error) {
      console.error('Erreur lors de l\'ajout de la demande de congé:', error);
    }
  };

  return (
    <div>
      <h2>Ajouter une demande de congé</h2>
      <form onSubmit={handleAddLeave}>
        <label>
          Type:
          <input type="text" name="type" value={newLeaveData.type} onChange={handleInputChange} />
        </label>
        <label>
          Date de début:
          <input type="date" name="dateDebut" value={newLeaveData.dateDebut} onChange={handleInputChange} />
        </label>
        <label>
          Date de fin:
          <input type="date" name="dateFin" value={newLeaveData.dateFin} onChange={handleInputChange} />
        </label>
        {/* Le champ employé doit être rempli avec l'ID de l'employé sélectionné */}
        <label>
          Employé:
          <input type="text" name="employe" value={newLeaveData.employe} onChange={handleInputChange} />
        </label>
        {/* Le champ statut est défini par défaut sur "En attente" */}
        <input type="hidden" name="statut" value="En attente" />
        <Link to="/employees"><button type="button">Liste des conges</button></Link>

        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
};

export default AddLeavePage;
