import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';

const UpdateLeavePage = () => {
  const { id } = useParams();
  const [leaveData, setLeaveData] = useState({
    type: '',
    dateDebut: '',
    dateFin: '',
    statut: '',
    employe: '',
    dateDemande: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchLeaveData();
  }, []);

  const fetchLeaveData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/leave/${id}`);
      setLeaveData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching leave data:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  const handleSub = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:3001/api/leave/${id}`, leaveData);
      console.log('Response:', response.data);
      // Redirection vers la liste des demandes de congé après la modification réussie
      // Vous pouvez utiliser useHistory ou utiliser un <Link> avec to="/leave"
    } catch (error) {
      console.error('Something went wrong!', error);
      setError(error.message);
    }
  };

  const handleChange = (e) => {
    setLeaveData({ ...leaveData, [e.target.name]: e.target.value });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Modifier une demande de congé</h2>
      <form onSubmit={handleSub}>
        <label>
          Type:
          <input type="text" name="type" value={leaveData.type} onChange={handleChange} />
        </label>
        <label>
          Date de début:
          <input type="date" name="dateDebut" value={leaveData.dateDebut} onChange={handleChange} />
        </label>
        <label>
          Date de fin:
          <input type="date" name="dateFin" value={leaveData.dateFin} onChange={handleChange} />
        </label>
        <label>
          Statut:
          <input type="text" name="statut" value={leaveData.statut} onChange={handleChange} />
        </label>
        <label>
          Employé:
          <input type="text" name="employe" value={leaveData.employe} onChange={handleChange} />
        </label>
        <label>
          Date de demande:
          <input type="date" name="dateDemande" value={leaveData.dateDemande} onChange={handleChange} />
        </label>

        <Link to="/leave"><button type="button">Liste des demandes de congé</button></Link>
        <button type="submit">Modifier</button>
      </form>
    </div>
  );
};

export default UpdateLeavePage;
