import React, { useState } from 'react';
import axios from 'axios';
import { Link,  } from 'react-router-dom'; // Importez Link depuis react-router-dom

const UpdateEmployeePage = ({ history }) => {
  const [newEmployeeData, setNewEmployeeData] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: '',
    departement: '',
    dateEmbauche: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewEmployeeData({
      ...newEmployeeData,
      [name]: value,
    });
  };
  const fetchEmployee = async () => {
    try {
      const id = localStorage.getItem('idTicket');
      const response = await axios.get(`http://localhost:3001/api/employee/${id}`);
      const { data } = response;
      console.log(response)
      if (data) {
        setNewEmployeeData(data);
      }
    } catch (error) {
      console.error('Error fetching employee:', error);
    }
  };

  useEffect(() => {
    fetchEmployee();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const id = localStorage.getItem('idTicket');
      await axios.put(`http://localhost:3001/api/employee/${id}`, newEmployeeData);
      navigate('/ticket');
    } catch (error) {
      console.error('Error updating ticket:', error);
    }
  };


  return (
    <div>
      <h2>Ajouter un employé</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom:
          <input type="text" name="nom" value={newEmployeeData.nom} onChange={handleChange} />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={newEmployeeData.prenom} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={newEmployeeData.email} onChange={handleChange} />
        </label>
        <label>
          Poste:
          <input type="text" name="poste" value={newEmployeeData.poste} onChange={handleChange} />
        </label>
        <label>
          Département:
          <input type="text" name="departement" value={newEmployeeData.departement} onChange={handleChange} />
        </label>
        <label>
          Date d'embauche:
          <input type="date" name="dateEmbauche" value={newEmployeeData.dateEmbauche} onChange={handleChange} />
        </label>
        {/* Utilisez Link pour créer le lien vers EmployeeComponent */}
        <Link to="/employees"><button type="button">Liste des employee</button></Link>
        <button type="submit">Ajouter</button>
        
      </form>
    </div>
  );
};

export default UpdateEmployeePage;
