import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AbsenceComponent = () => {
  const [absences, setAbsences] = useState([]);

  useEffect(() => {
    // Fonction pour charger les absences depuis l'API
    const fetchAbsences = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/absences');
        setAbsences(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des absences:', error);
      }
    };

    // Appeler la fonction pour charger les absences au chargement du composant
    fetchAbsences();
  }, []);

  return (
    <div>
      <h2>Liste des absences</h2>
      <ul>
        {absences.map(absence => (
          <li key={absence.id}>
            Date: {absence.date}, Motif: {absence.motif}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AbsenceComponent;
