import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LeaveComponent = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);

  useEffect(() => {
    // Fonction pour charger les demandes de congé depuis l'API
    const fetchLeaveRequests = async () => {
      try {
        const response = await axios.get('/api/leave');
        setLeaveRequests(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des demandes de congé:', error);
      }
    };

    // Appeler la fonction pour charger les demandes de congé au chargement du composant
    fetchLeaveRequests();
  }, []);

  return (
    <div>
      <h2>Liste des demandes de congé</h2>
      <ul>
        {leaveRequests.map(request => (
          <li key={request.id}>
            Employé: {request.employe.nom} {request.employe.prenom}, Type: {request.type}, Début: {request.dateDebut}, Fin: {request.dateFin}, Statut: {request.statut}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LeaveComponent;
