import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeComponent.css';

const EmployeeComponent = () => {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [updatedEmployeeData, setUpdatedEmployeeData] = useState({
    nom: '',
    prenom: '',
    email: '',
    poste: '',
    departement: '',
    dateEmbauche: ''
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/employee');
      setEmployees(response.data);
    } catch (error) {
      console.error('Erreur lors du chargement des employés:', error);
    }
  };

  const handleDeleteEmployee = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/employee/${id}`);
      fetchEmployees(); // Recharger la liste des employés après la suppression
    } catch (error) {
      console.error('Erreur lors de la suppression de l\'employé:', error);
    }
  };

  const handleUpdateEmployee = async (id) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/employee/${id}`);
      setSelectedEmployee(response.data);
    } catch (error) {
      console.error('Erreur lors de la récupération des données de l\'employé:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedEmployeeData({ ...updatedEmployeeData, [name]: value });
  };

  const handleUpdateSubmit = async () => {
    try {
      await axios.put(`http://localhost:3001/api/employee/${selectedEmployee._id}`, updatedEmployeeData);
      fetchEmployees(); // Recharger la liste des employés après la mise à jour
      setSelectedEmployee(null); // Réinitialiser l'employé sélectionné
    } catch (error) {
      console.error('Erreur lors de la mise à jour de l\'employé:', error);
    }
  };

  return (
    <div>
      <h2>Liste des employés</h2>
      <button onClick={handleAddEmployee}>Ajouter un employé</button>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Prénom</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee._id}>
              <td>{employee.nom}</td>
              <td>{employee.prenom}</td>
              <td>{employee.email}</td>
              <td>
                <button onClick={() => handleDeleteEmployee(employee._id)}>Supprimer</button>
                <button onClick={() => handleUpdateEmployee(employee._id)}>Mettre à jour</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedEmployee && (
        <div>
          <h2>Modifier l'employé</h2>
          <form onSubmit={handleUpdateSubmit}>
            <label>
              Nom:
              <input type="text" name="nom" value={updatedEmployeeData.nom} onChange={handleInputChange} />
            </label>
            <label>
              Prénom:
              <input type="text" name="prenom" value={updatedEmployeeData.prenom} onChange={handleInputChange} />
            </label>
            <label>
              Email:
              <input type="text" name="email" value={updatedEmployeeData.email} onChange={handleInputChange} />
            </label>
            <label>
              Poste:
              <input type="text" name="poste" value={updatedEmployeeData.poste} onChange={handleInputChange} />
            </label>
            <label>
              Département:
              <input type="text" name="departement" value={updatedEmployeeData.departement} onChange={handleInputChange} />
            </label>
            <label>
              Date d'embauche:
              <input type="date" name="dateEmbauche" value={updatedEmployeeData.dateEmbauche} onChange={handleInputChange} />
            </label>
            <button type="submit">Mettre à jour</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default EmployeeComponent;
