import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'; // Importez Link depuis react-router-dom

const UpdateEmployeePage = ({ employeeId }) => {
    const [employeeData, setEmployeeData] = useState({
        nom: '',
        prenom: '',
        email: '',
        poste:'',
        departement: '',
        dateEmbauche: ''
    });

    useEffect(() => {
        const fetchEmployeeData = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/api/employee/${employeeId}`);
                const employeeData = response.data.dat;
                setEmployeeData(employeeData);
            } catch (error) {
                console.error('Error fetching client data:', error);
            }
        };

        fetchEmployeeData();
    }, [employeeId]);

    const handleSub = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put(`http://localhost:3001/api/employee/${employeeId}`, employeeData);
            console.log('Response:', response.data);
            console.log('idddd:', employeeId);
        } catch (error) {
            console.error('Something went wrong!', error);
        }
    };

    const handleChange = (e) => {
        setEmployeeData({ ...employeeData, [e.target.name]: e.target.value });
    };

  return (
    <div>
      <h2>Modifier un employé</h2>
      <form onSubmit={handleSub}>
        <label>
          Nom:
          <input type="text" name="nom" value={employeeData.nom} onChange={handleChange} />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={employeeData.prenom} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={employeeData.email} onChange={handleChange} />
        </label>
        <label>
          Poste:
          <input type="text" name="poste" value={employeeData.poste} onChange={handleChange} />
        </label>
        <label>
          Département:
          <input type="text" name="departement" value={employeeData.departement} onChange={handleChange} />
        </label>
        <label>
          Date d'embauche:
          <input type="date" name="dateEmbauche" value={employeeData.dateEmbauche} onChange={handleChange} />
        </label>
        
        <Link to="/employees"><button type="button">Liste des employés</button></Link>
        <button type="submit">Modifier</button>
        
      </form>
    </div>
  );
};

export default UpdateEmployeePage;
