import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'; // Importez Link depuis react-router-dom

const UpdateEmployeePage = ({ employeeId }) => {
    const [clientData, setClientData] = useState({
        name: '',
        email: '',
        password: '',
        phone: '',
        address: ''
    });

    useEffect(() => {
        const fetchClientData = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/api/clients/${clientId}`);
                const clientData = response.data;
                setClientData(clientData);
            } catch (error) {
                console.error('Error fetching client data:', error);
            }
        };

        fetchClientData();
    }, [clientId]);

    const handleSub = async () => {
        try {
            const response = await axios.put(`http://localhost:3001/api/clients/${clientId}`, clientData);
            console.log('Response:', response.data);
            console.log('idddd:', clientId);
        } catch (error) {
            console.error('Something went wrong!', error);
        }
    };

    const handleChange = (e) => {
        setClientData({ ...clientData, [e.target.name]: e.target.value });
    };

  return (
    <div>
      <h2>Ajouter un employé</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom:
          <input type="text" name="nom" value={newEmployeeData.nom} onChange={handleChange} />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={newEmployeeData.prenom} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={newEmployeeData.email} onChange={handleChange} />
        </label>
        <label>
          Poste:
          <input type="text" name="poste" value={newEmployeeData.poste} onChange={handleChange} />
        </label>
        <label>
          Département:
          <input type="text" name="departement" value={newEmployeeData.departement} onChange={handleChange} />
        </label>
        <label>
          Date d'embauche:
          <input type="date" name="dateEmbauche" value={newEmployeeData.dateEmbauche} onChange={handleChange} />
        </label>
        {/* Utilisez Link pour créer le lien vers EmployeeComponent */}
        <Link to="/employees"><button type="button">Liste des employee</button></Link>
        <button type="submit">Ajouter</button>
        
      </form>
    </div>
  );
};

export default UpdateEmployeePage;
