import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AttendanceComponent = () => {
  const [attendances, setAttendances] = useState([]);

  useEffect(() => {
    const fetchAttendances = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/attendance');
        const attendanceWithEmployees = await Promise.all(
          response.data.map(async (attendance) => {
            try {
              const employeeResponse = await axios.get(`http://localhost:3001/api/employee/${attendance.employe}`);
              const employeeData = employeeResponse.data; // Données de l'employé
              return { ...attendance, employe: employeeData }; // Ajout des données de l'employé à l'absence
            } catch (error) {
              console.error('Erreur lors de la récupération des données de l\'employé:', error);
              return attendance; // Retourner l'absence sans les données de l'employé en cas d'erreur
            }
          })
        );
        setAttendances(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des enregistrements de pointage:', error);
      }
    };

    fetchAttendances();
  }, []);

  const handleDeleteAttendance = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/attendance/${id}`);
      setAttendances(attendances.filter((attendance) => attendance._id !== id));
    } catch (error) {
      console.error('Erreur lors de la suppression de l\'enregistrement de pointage:', error);
    }
  };
  const handleAddAbsence = async () => {
    try {
      const newAttendanceData = {/* Les données de la nouvelle absence à ajouter */};
      const response = await axios.post('http://localhost:3001/api/attendance', newAttendanceData); // Ajouter l'absence
      const newAttendance = response.data;
      setAbsences([newAttendance, ...attendances]); // Ajouter la nouvelle absence au début de la liste
      // Rediriger ou effectuer d'autres actions après l'ajout de l'absence
    } catch (error) {
      console.error('Erreur lors de l\'ajout de l\'absence:', error);
    }
  };

  return (
    <div>
      <h2>Liste des enregistrements de pointage</h2>
      <Link to="/addAttendance"><button type="button">Ajouter un enregistrement</button></Link>
      <table>
        <thead>
          <tr>
            <th>Employé</th>
            <th>Date</th>
            <th>Heures travaillées</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {attendances.map((attendance) => (
            <tr key={attendance._id}>
              <td>{attendance.employe && `${attendance.employe.nom} ${attendance.employe.prenom}`}</td>
              <td>{new Date(attendance.date).toLocaleDateString()}</td>
              <td>{attendance.heuresTravaillees}</td>
              <td>
                <button onClick={() => handleDeleteAttendance(attendance._id)}>Supprimer</button>
                <Link to={`/updateAttendance/${attendance._id}`}><button>Mettre à jour</button></Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceComponent;
