import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AttendanceComponent = () => {
  const [attendances, setAttendances] = useState([]);

  useEffect(() => {
    const fetchAttendances = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/attendance');
        setAttendances(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des enregistrements de pointage:', error);
      }
    };

    fetchAttendances();
  }, []);

  const handleDeleteAttendance = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/attendance/${id}`);
      setAttendances(attendances.filter(attendance => attendance._id !== id));
    } catch (error) {
      console.error('Erreur lors de la suppression de l\'enregistrement de pointage:', error);
    }
  };

  return (
    <div>
      <h2>Liste des enregistrements de pointage</h2>
      <Link to="/addAttendance"><button type="button">Ajouter un enregistrement de pointage</button></Link>
      <ul>
        {attendances.map(attendance => (
          <li key={attendance._id}>
            Employé: {attendance.employe && attendance.employe.nom}, Date: {attendance.date}, Heures: {attendance.heuresTravaillees}
            <button onClick={() => handleDeleteAttendance(attendance._id)}>Supprimer</button>
            <Link to={`/updateAttendance/${attendance._id}`}><button>Mettre à jour</button></Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AttendanceComponent;
