// RegisterPage.js

import React, { useState } from 'react';
import axios from 'axios';

const RegisterPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        
    });

    const handleSub = async () => {
        try {
            const response = await axios.post('http://localhost:3001/api/clients', formData);
            console.log('Response:', response.data);
            // Réinitialiser le formulaire après l'envoi des données
            setFormData({
                name: '',
                email: '',
                password: '',
                phone: '',
                address: ''
            });
        } catch (error) {
            console.error('Something went wrong!', error);
        }
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.id]: e.target.value });
    };

    return (
        
        
        <div style={{ maxWidth: '400px', margin: 'auto', marginTop: '50px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px' }}>
            <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Ajouter un client</h2>
            <form>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Nom & Prénom</label>
                    <input type="text" className="form-control" id="name" value={formData.name} onChange={handleChange} style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
                </div>
                
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email </label>
                    <input type="text" className="form-control" id="email" value={formData.email} onChange={handleChange} style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input type="password" className="form-control" id="password" value={formData.password} onChange={handleChange} style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
                </div>
                <div className="mb-3">
                    <label htmlFor="phone" className="form-label">Téléphone</label>
                    <input type="text" className="form-control" id="phone" value={formData.phone} onChange={handleChange} style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
                </div>
                <div className="mb-3">
                    <label htmlFor="address" className="form-label">Adresse</label>
                    <input type="text" className="form-control" id="address" value={formData.address} onChange={handleChange} style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }} />
                </div>
                
                <button type="button" className="btn btn-primary" onClick={handleSub} style={{ width: '100%', padding: '10px', borderRadius: '5px', border: 'none' }}>Submit</button>
            </form>
        </div> 
    );
};

export default RegisterPage;
