import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';

const UpdateAbsencePage = () => {
  const { id } = useParams();
  const [absenceData, setAbsenceData] = useState({
    date: '',
    motif: '',
    heuresManquees: '',
    employe: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAbsenceData();
  }, []);

  const fetchAbsenceData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/absence/${id}`);
      setAbsenceData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching absence data:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  const handleSub = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:3001/api/absence/${id}`, absenceData);
      console.log('Response:', response.data);
      // Redirection vers la liste des absences après la modification réussie
      // Vous pouvez utiliser useHistory ou utiliser un <Link> avec to="/absences"
    } catch (error) {
      console.error('Something went wrong!', error);
      setError(error.message);
    }
  };

  const handleChange = (e) => {
    setAbsenceData({ ...absenceData, [e.target.name]: e.target.value });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Modifier une absence</h2>
      <form onSubmit={handleSub}>
        <label>
          Date:
          <input type="date" name="date" value={absenceData.date} onChange={handleChange} />
        </label>
        <label>
          Motif:
          <input type="text" name="motif" value={absenceData.motif} onChange={handleChange} />
        </label>
        <label>
          Heures manquées:
          <input type="number" name="heuresManquees" value={absenceData.heuresManquees} onChange={handleChange} />
        </label>
        <label>
          Employé:
          <input type="text" name="employe" value={absenceData.employe} onChange={handleChange} />
        </label>

        <Link to="/absences"><button type="button">Liste des absences</button></Link>
        <button type="submit">Modifier</button>
      </form>
    </div>
  );
};

export default UpdateAbsencePage;
