// RegisterPage.js

import React, { useState } from 'react';
import axios from 'axios';

const RegisterPage = () => {
    const [formData, setFormData] = useState({ username: '', password: '' });

   

    const handleSubmit = async () => {
        //e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3002/api/auth/register', formData);
            console.log('hello')
            if (response && response.data) {
                console.log('test', response);
                setFormData({
                    username: '',
                    password: ''
                })
                // Rediriger l'utilisateur vers une autre page après l'inscription
                // Exemple : history.push('/login');
            } else {
                console.error('Réponse inattendue du serveur');
            }
        } catch (error) {
            console.error('Erreur lors de l\'inscription:', error.response ? error.response.data.message : error.message);
        }
    };
    
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };
    return (
        <div>
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Username:
                    <input type="text" name="username" value={formData.username} onChange={handleChange} />
                </label>
                <label>
                    Password:
                    <input type="password" name="password" value={formData.password} onChange={handleChange} />
                </label>
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default RegisterPage;
