import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'; // Importez Link depuis react-router-dom

const UpdateEmployeePage = () => {
    const navigate = useNavigate();
    const [newEmployeeData, setEmployeeData] = useState({
        nom: '',
        prenom: '',
        email: '',
        poste:'',
        departement: '',
        dateEmbauche: ''
    });

    
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData({
      ...newEmployeeData,
      [name]: value,
    });
  };

  const fetchEmployee = async () => {
    try {
      const id = localStorage.getItem('idTicket');
      const response = await axios.get(`http://localhost:3001/api/employee/${id}`);
      const { data } = response;
      console.log(response)
      if (data) {
        setEmployeeData(data);
      }
    } catch (error) {
      console.error('Error fetching ticket:', error);
    }
  };

  useEffect(() => {
    fetchEmployee();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const id = localStorage.getItem('idTicket');
      await axios.put(`http://localhost:3001/api/employee/${id}`, newEmployeeData);
      navigate('/ticket');
    } catch (error) {
      console.error('Error updating ticket:', error);
    }
  };

  return (
    <div>
      <h2>Modifier un employé</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom:
          <input type="text" name="nom" value={employeeData.nom} onChange={handleChange} />
        </label>
        <label>
          Prénom:
          <input type="text" name="prenom" value={employeeData.prenom} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={employeeData.email} onChange={handleChange} />
        </label>
        <label>
          Poste:
          <input type="text" name="poste" value={employeeData.poste} onChange={handleChange} />
        </label>
        <label>
          Département:
          <input type="text" name="departement" value={employeeData.departement} onChange={handleChange} />
        </label>
        <label>
          Date d'embauche:
          <input type="date" name="dateEmbauche" value={employeeData.dateEmbauche} onChange={handleChange} />
        </label>
        
        <Link to="/employees"><button type="button">Liste des employés</button></Link>
        <button type="submit">Modifier</button>
        
      </form>
    </div>
  );
};

export default UpdateEmployeePage;
